INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (1, 1, 'Маска Зорро', 'Стареющий герой, отдавший все силы борьбе за справедливость, проведший десятилетия в темнице, потерявший жену и дочь, передает свои знания, умения и черную маску молодому отчаянному бойцу, который будет сражаться с теми, кто вершит зло.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (2, 2, 'Балто', 'Наполовину лайка, наполовину волк, Балто и сам не знает кто он такой на самом деле. Всеми гонимый бродяга в ледяной пустыне Аляски. Однажды случается несчастье. Эпидемия дифтерии охватила детей небольшого поселка. Теперь только Балто может найти упряжку и спасти детей.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (3, 3, 'Каспер', 'Каспер, маленькое дружелюбное привидение, не желает походить на своих хулиганствующих собратьев. Его дружба с девочкой-подростком Кэт помогает ей и ее отцу, доктору Харви, справиться с опасностями, поджидающими их в населенном призраками доме.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (4, 1, 'Мы вернулись! История динозавра', 'Добрый профессор с говорящим именем Благовзгляд отправился во времена динозавров на своём чудесном дирижабле, способном перемещаться во времени. Поймав четырёх злобных динозавров, профессор накормил их специальным препаратом, превратившим монстров в говорящих весельчаков.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (5, 2, 'Назад в будущее', 'Из газет 1955 года Марти узнает, что доктор Браун погиб еще сто лет назад. Несмотря на просьбы Дока не навещать его в XIX веке, Марти вновь хочет вмешаться в ход событий и на машине времени уносится в 1885 год, навстречу Дикому Западу.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (6, 3, 'Сны Акиры Куросавы', 'Рассказ о том, как молодой Куросава попадает в картину Винсента Ван Гога и встречается там с самим автором.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (7, 1, 'Папа', 'Преуспевающий бизнесмен с Уолл-стрит Джон узнает о том, что его 75-летний отец Джейк при смерти. Джон возвращается в дом своих родителей и начинает ухаживать за пожилым отцом.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (8, 2, 'Кто подставил кролика Роджера', 'Частный детектив расследует загадочное преступление, в котором подозревается мультипликационный персонаж кролик Роджер! Сыщик попадает в непредсказуемый мир, где его ожидают нарисованные злодеи с настоящими револьверами, говорящий автомобиль и соблазнительная мультяшная красотка.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (9, 3, 'Американская история', 'Фильм повествует об удивительных приключениях маленького отважного мышонка по имени Фивел.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (10, 1, 'Гремлины', 'Очаровательный пушистый зверек Магвай способен растрогать даже камень. Вдобавок, у него абсолютный слух. Только будьте осторожны: он может погибнуть от солнечного света, на него нельзя брызгать водой, а что будет, если накормить его после полуночи, просто страшно представить.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (11, 2, 'Заблудшие души', 'Главная героиня узнает о существовании таинственного дьявольского заклятия, воплощение которого грозит человечеству приходом Сатаны. Для того, чтобы предотвратить пришествие Зла, ей надо найти человека, в тело которого собирается вселиться дьявол, и убедить его, что его жизни угрожает реальная опасность.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (12, 3, 'Абдулладжан, или Посвящается Стивену Спилбергу', 'Сторож сельмага, посмотрев в местном видеосалоне фильм Стивена Спилберга, решил написать письмо в Голливуд о том, что произошло в его кишлаке.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (13, 1, 'U2: Rattle and Hum', 'Документальный фильм об ирландской рок-группе U2, саундтрек к которому стал шестым студийным альбомом и вышел в том же году.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (14, 2, 'Лица', 'Ричард и Мария женаты уже четырнадцать лет, но их брак переживает не лучшие времена. Устав друг от друга, они решают поискать забытую остроту ощущений на стороне.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (15, 3, 'Братья Блюз', 'Братья-музыканты Джейк и Элвуд Блюзы узнают, что их родная церковь будет снесена, если за нее вовремя не внесут плату в 5 тысяч долларов. На поиск денег у братьев не много времени, и они решают дать концерт.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (16, 1, 'Дон Жуан де Марко', 'Романтическая мелодрама о взаимоотношениях стареющего врача-психиатра и его молодого пациента, который искренне считает себя Дон Жуаном.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (17, 2, 'Формула', 'Во время Второй мировой войны Германия разработала метод добычи дешевого и экологически безопасного синтетического топлива. Но после поражения Третьего рейха феноменальная формула исчезла.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (18, 3, 'Последнее танго в Париже', 'Действие происходит в Париже конца шестидесятых. Сорокапятилетний американец потрясен недавней смертью жены, он считает себя виновным и находится в состоянии глубокой депрессии.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (19, 1, 'Ночь следующего дня', 'В парижском аэропорту двое отморозков похищают девушку и отвозят её в дом на побережье, где находится штаб-квартира всей банды. После получения запрошенного выкупа, и без того натянутые отношения между похитителями сразу разлаживаются.')
INSERT INTO [dbo].[film] (id_film, manager_id, name, description) VALUES (20, 2, 'Погоня', 'Штат Техас. В тихом провинциальном городке следит за порядком добропорядочный шериф Колдер. Однажды покой городка нарушает известие о том, что из тюрьмы бежал Баббер, который желает свести старые счеты кое с кем из жителей города.')
